import { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'
import {Link, useNavigate} from 'react-router-dom'

function Signup() {
    const [user, setUser] = useState()
    // const [email, setEmail] = useState()
    // const [password, setPassword] = useState()
    const navigate = useNavigate()
    const [isValidPhoneNumber, setIsValidPhoneNumber] = useState(true); // État pour suivre si le numéro de téléphone est valide

    const handleSubmit = (e) => {
        e.preventDefault()
        const phoneNumberPattern = /^0[1-9]([-. ]?[0-9]{2}){4}$/;
        if (!phoneNumberPattern.test(user)) {
          // Afficher un message d'erreur ou prendre une action appropriée si le numéro n'est pas valide
          setIsValidPhoneNumber(false);
          return;
      }
      setIsValidPhoneNumber(true);
        axios.post('http://localhost:4000/register', {user})
        .then(res => {
            navigate('/login')
        }).catch(err => console.log(err))
    }

  return (
    <div className="d-flex justify-content-center align-items-center bg-secondary vh-100">
      <div className="bg-white p-3 rounded w-25">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
        <div className="mb-3">
            <label htmlFor="email">
              <strong>Phone number</strong>
            </label>
            <input
              type="text"
              placeholder="Enter Phone number"
              autoComplete="off"
              name="user"
              className="form-control rounded-0"
              onChange={(e) => setUser(e.target.value)}
            />
          </div>
          {/* <div className="mb-3">
            <label htmlFor="email">
              <strong>Email</strong>
            </label>
            <input
              type="email"
              placeholder="Enter Email"
              autoComplete="off"
              name="email"
              className="form-control rounded-0"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div> */}
          {/* <div className="mb-3">
            <label htmlFor="email">
              <strong>Password</strong>
            </label>
            <input
              type="password"
              placeholder="Enter Password"
              name="password"
              className="form-control rounded-0"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div> */}
          <button type="submit" className="btn btn-success w-100 rounded-0">
            Register
          </button>
          </form>
          <p>Already Have an Account</p>
          <Link to="/login" className="btn btn-default border w-100 bg-light rounded-0 text-decoration-none">
            Login
          </Link>
        
      </div>
    </div>
  );
}

export default Signup;
